	$(".modal-opener").each(function(){
	$(this).click(function(){
		var datamodal = $(this).attr("data-modal");
		var modaltarget = $('.layer[modal-target="' + datamodal + '"]');
		if(modaltarget.length){
			modaltarget.fadeIn();
		}
		$("body").addClass("modal-open");
	})	
	});
	$(".modal-close").each(function(){
		$(this).click(function(){
			var modalclose = $(this).attr("modal-close");
			var modaltarget = $('.layer[modal-target="' + modalclose + '"]');
			if(modaltarget.length){
				modaltarget.fadeOut();
			}
			$("body").removeClass("modal-open");		
		})	
	});
	
	
	
	
	
