$(".pg-ex2 ul li img").each(function(){
		$(this).click(function(){
			var pgdatasrc = $(this).attr("data-src");
			var captitle = $(this).attr("title");
			$('.pg-ex2-preview img').attr('src', pgdatasrc);
			$('.pg-ex2-preview').children(".imagecaption").html(captitle);
			if (captitle == "") {
				$('.pg-ex2-preview').children(".imagecaption").hide();
			}
			else{
				$('.pg-ex2-preview').children(".imagecaption").show();
				$('.pg-ex2-preview').children(".imagecaption").html(captitle);
			}
		});
	});


