//Notification
$(".notification-opener").each(function(){
	$(this).click(function(){
		var datanotification = $(this).attr("data-notification");
		var datanotificationbox = $('.notification-box[data-notificationbox="' + datanotification + '"]');
		var notificationboxposition = $('.notification-box[data-notificationbox="' + datanotification + '"]').attr("data-position");
		if(datanotificationbox.length){
			datanotificationbox.addClass("notification-active-"+ notificationboxposition);
		}
		setTimeout(function(){
			datanotificationbox.removeClass("notification-active-"+ notificationboxposition);
		},20000);
	})	
	
});
$(".notification-close").each(function(){
	$(this).click(function(){
		var datanotificationclose = $(this).attr("data-notificationclose");
		var datanotificationbox = $('.notification-box[data-notificationbox="' + datanotificationclose + '"]');
		var notificationboxposition = $('.notification-box[data-notificationbox="' + datanotificationclose + '"]').attr("data-position");
		if(datanotificationbox.length){
			datanotificationbox.removeClass("notification-active-"+ notificationboxposition);
		}
	})	
});



	
